import React, { Component } from 'react'

import AuthServices from '../../services/auth.services'
import FilesServices from '../../services/files.services'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'


class SignupForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username: '',
            password: '',
            confirmPassword: '',
            email: ''
        }
        this.AuthServices = new AuthServices()
        this.filesServices = new FilesServices()
    }


    finishAction = () => {
        this.props.closeModal()
    }
    postUser = () => {
        this.AuthServices.signup(this.state)
            .then(theLoggedNewUser => {
                this.setState({ username: '', password: '', confirmPassword: '', email: '' })
                this.props.setTheUser(theLoggedNewUser)
                // this.finishAction()
            })
            .catch(err => console.log({ err }))
    }

    handleChange = e => {
        let { name, value } = e.target
        this.setState({ [name]: value })
    }

    handleSubmit = e => {
        e.preventDefault()
        this.postUser()
    }


    render() {

        return (
            <Form onSubmit={this.handleSubmit}>
                <Form.Group>
                    <Form.Label>Usuario</Form.Label>
                    <Form.Control type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Contraseña</Form.Label>
                    <Form.Control type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Confirmar contraseña</Form.Label>
                    <Form.Control type="password" name="confirmPassword" value={this.state.confirmPassword} onChange={this.handleChange} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" name="email" value={this.state.email} onChange={this.handleChange} />
                </Form.Group>
                <Button variant="dark" type="submit">Crear cuenta</Button>
            </Form>
        )
    }
}


export default SignupForm
